import { useState } from 'react'

const Button = ({ onClick, text }) => {
  return (
    <button onClick={onClick}>{text}</button>
  )
}

const Dispaly = ({ text, value }) => {
  return (
    <p>{text} {value}</p>
  )
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const handleClickGood = () => {
    console.log("Good button clicked")
    setGood(good + 1)
  }
  const handleClickNeutral = () => {
    console.log("Neutral button clicked")
    setNeutral(neutral + 1)
  }
  const handleClickBad = () => {
    console.log("Bad button clicked")
    setBad(bad + 1)
  }

  all = (good + neutral + bad)
  average = (good - bad)/all
  positive = good/all

  return (
    <div>
      <h3>give feedback</h3>
      <Button onClick={handleClickGood} text="good"></Button>
      <Button onClick={handleClickNeutral} text="neutral"></Button>
      <Button onClick={handleClickBad} text="bad"></Button>
      <h3>statistics</h3>
      <Dispaly text="good" value={good}></Dispaly>
      <Dispaly text="neutral" value={neutral}></Dispaly>
      <Dispaly text="bad" value={bad}></Dispaly>
      <Dispaly text="all" value={all}></Dispaly>
      <Dispaly text="all" val={average}></Dispaly>
      <Dispaly text="all" value={good + neutral + bad}></Dispaly>
    </div>
  );
}

export default App;
